'    from expe_expenses expe ',
'   where expe.expe_id = l_business_ref',
'       ;',
'  return l_expe_purpose;',
'end get_expe_purpose;',
'',
'function get_mailto',
'return varchar2',
'as',
'  l_business_ref      number;',
'  l_expe_submitted_by expe_expenses.expe_submitted_by%type;',
