'  else',
'    l_return:= false;',
'  end case;',
'',
'  return l_return;',
'end check_status;',
'',
'function check_limit_exceeded',
'( p_expe_id in number ',
') return boolean',
'is',
'',
'  c_amount_limit  constant number:= 50;',
'  l_return        number;',
'',
'begin',
'  select count(*)',
'    into l_return',
'    from expe_expenses expe',
'   where expe.expe_id = p_expe_id',
'     and expe.expe_amount > c_amount_limit',
'       ;',
'',
'  return (l_return = 1);',
'end check_limit_exceeded;',
'',
'procedure do_payment',
'is',
'  l_account         number;',
'  l_amount          number;',
'  l_payment_method  varchar2(20 char);',
