'create or replace package body expe_p0006',
'as ',
' ',
'procedure process_ig',
'( p_prcs_id             in number',
', p_sbfl_id             in number',
', p_step_key            in varchar2',
', p_expe_account        in varchar2',
', p_expe_payment_method in varchar2',
