prompt --application/deployment/install/install_05_dml
begin
wwv_flow_api.create_install_script(
 p_id=>wwv_flow_api.id(4295602558475584207)
,p_install_id=>wwv_flow_api.id(4311900486557790898)
,p_name=>'05_DML'
,p_sequence=>50
,p_script_type=>'INSTALL'
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'',
'  l_expe_id        expe_expenses.expe_id%type;',
'  l_prcs_id        number;',
'  l_sbfl_id        number;',
'  l_sbfl_step_key  varchar2(20 char);',
'  l_counter        integer;',
'  l_amount         number;',
'',
'begin',
'  for l_counter in 1 .. 10',
'  loop',
'    l_amount:= trunc(dbms_random.value(10,200));',
'    ',
'    insert into expe_expenses',
'    ( expe_invoice_dd',
'    , expe_amount',
'    , expe_purpose',
'    , expe_justification',
'    , expe_status',
'    ) ',
'    values',
'    ( sysdate',
'    , l_amount',
'    , case round(dbms_random.value(1,4)) ',
'      when 1 then ''gasoline'' ',
'      when 2 then ''park ticket'' ',
'      when 3 then ''hotel'' ',
'      when 4 then ''lunch'' ',
'      end',
'    , ''Company ABC''',
'    , ''submitted''',
'    )',
'    returning expe_id into l_expe_id',
'    ;',
'',
'    l_prcs_id:=',
'      flow_api_pkg.flow_create',
'      ( pi_dgrm_name => ''Expense Claims''',
'      , pi_prcs_name => ''Expense Claim '' || l_counter',
'      );',
'',
'    flow_api_pkg.flow_start',
'    ( p_process_id => l_prcs_id',
'    );',
'',
'    flow_process_vars.set_var',
'    ( pi_prcs_id   => l_prcs_id',
'    , pi_var_name  => ''BUSINESS_REF''',
'    , pi_vc2_value => l_expe_id ',
'    );',
'    ',
'    flow_process_vars.set_var',
'    ( pi_prcs_id   => l_prcs_id',
'    , pi_var_name  => ''APPLICATION_ID''',
'    , pi_vc2_value => apex_application.g_flow_id',
'    );',
'    ',
'    select sbfl_id',
'         , sbfl_step_key',
'      into l_sbfl_id',
'         , l_sbfl_step_key',
'      from flow_subflows_vw sbfl',
'     where sbfl.sbfl_prcs_id = l_prcs_id',
'         ;',
'  end loop;',
'end;'))
);
end;
