 p_id=>wwv_flow_api.id(4287602787095635633)
,p_install_id=>wwv_flow_api.id(4311900486557790898)
,p_name=>'Check for Flows for APEX 22.2.x'
,p_sequence=>10
,p_check_type=>'FUNCTION_BODY'
,p_check_condition=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'',
'  l_check_success          boolean:= false;',
'  l_flows_found            number;',
'  l_flows_actual_version   varchar2(10 char);',
'  c_flows_required_version varchar2(10 char) := ''22.2'';',
'',
'begin',
'  -- check if package exists',
'  select count(*)',
'    into l_flows_found ',
'    from user_procedures uprc',
'   where uprc.object_name = ''FLOW_CONSTANTS_PKG''',
'     and uprc.procedure_name is null',
'       ;',
'',
'  if l_flows_found = 1',
'  then',
'    -- check if correct version is installed',
'    execute immediate ''begin :version := substr(flow_constants_pkg.gc_version, 1, 4); end;'' using out l_flows_actual_version;',
' ',
'    if l_flows_actual_version = c_flows_required_version',
'    then',
'      l_check_success := true;',
'    else',
'      l_check_success := false;',
'    end if;',
'',
'  else',
'    l_check_success := false;',
'  end if;',
'',
'  return l_check_success;',
'',
'exception',
'  when others',
'  then',
'    return l_check_success;',
'end;'))
,p_failure_message=>'Please install the extension "Flows for APEX 22.1.x" first.'
