,p_plug_template=>wwv_flow_imp.id(39113586205370230)
,p_plug_display_sequence=>40
,p_include_in_reg_disp_sel_yn=>'Y'
,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with qry_simpleform as',
'(',
'    select *',
'    from flow_objects objt, json_table( objt.objt_attributes',
'        , ''$''',
'            columns',
'            task_type varchar2(4000) path ''$.taskType''',
'        ) jt',
'    where',
'        task_type = ''apexSimpleForm''',
'), qry_tasks as',
'(',
'      select distinct',
'           manager,',
'           amct.app_id,',
'           amct.task_id,',
'           amct.task_def_id,',
'           amct.task_def_name,',
'           amct.task_def_static_id,',
'           subject,',
'           case when qs.task_type is not null then qs.task_type',
'                when amct.task_type is not null then amct.task_type',
'                else null',
'           end as task_type,',
'           details_app_id,',
'           details_app_name,',
'           details_link_target,',
'           due_on,',
'           due_in_hours,',
'           due_in,',
'           due_code,',
'           priority,',
'           priority_level,',
'           initiator,',
'           initiator_lower,',
'           actual_owner,',
'           actual_owner_lower,',
'           state_code,',
'           state,',
'           is_completed,',
'           outcome_code,',
'           outcome,',
'           badge_css_classes,',
'           badge_text,',
'           created_ago_hours,',
'           created_ago,',
'           created_by,',
'           created_on,',
'           to_char(created_on, ''DD.MM.YYYY HH:MI:SS'') as created_on_html,',
'           last_updated_by,',
'           last_updated_on,',
'           coalesce(amct.process_id,to_number(tapa.param_value)) as process_id,',
'           subflow_id,',
'           step_key,',
'           objt_id,',
'           inst_id,',
'           case when inst_id is not null then 1 else 0 end as instance_found,',
'           case when ftpl_id is null then ''u-hidden'' else '''' end as template_found',
'      from flow_apex_my_combined_task_list_vw amct',
'      left join apex_task_parameters          tapa      on tapa.task_id = amct.task_id and param_static_id = ''PROCESS_ID''',
'      left join flow_subflows_vw              sbfl      on sbfl.sbfl_id = amct.subflow_id',
'      left join qry_simpleform                qs        on qs.objt_dgrm_id = sbfl.sbfl_dgrm_id and qs.objt_bpmn_id = sbfl.sbfl_current',
'      left join form_started_processes_vw     form_proc on form_proc.prcs_id = coalesce(amct.process_id,to_number(tapa.param_value))',
'      left join form_instances                form_inst on form_inst.inst_id = to_number(form_proc.prcs_business_ref default -1 on conversion error)',
'      left join form_templates                ftpl      on ftpl.ftpl_id = form_inst.inst_ftpl_id',
')',
'select',
'    *',
'from',
'    qry_tasks',
'where',
'    instance_found != 0',
'order by created_on desc'))
,p_lazy_loading=>false
,p_plug_source_type=>'NATIVE_CARDS'
,p_plug_query_num_rows_type=>'SCROLL'
,p_plug_query_no_data_found=>'No Tasks'
,p_show_total_row_count=>false
);
