,p_name=>'Init View'
,p_sequence=>20
,p_script_type=>'INSTALL'
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "FORM_STARTABLE_DIAGRAMS_VW" ("DGRM_ID", "DGRM_NAME", "DGRM_SHORT_DESCRIPTION", "DGRM_DESCRIPTION", "DGRM_ICON", "DGRM_CATEGORY", "DISPLAY_VALUE") AS ',
'  select dgrm.dgrm_id',
'       , dgrm.dgrm_name',
'       , dgrm.dgrm_short_description',
'       , dgrm.dgrm_description',
'       , coalesce(dgrm.dgrm_icon, ''no-icon'') as dgrm_icon',
'       , dgrm.dgrm_category',
'       , dgrm_name || '' ('' || dgrm_version || '' - '' || dgrm_status || '')'' as display_value',
'  from flow_startable_diagrams_vw dgrm',
' where dgrm.dgrm_status = ''released''',
'   and (',
'            (',
'                dgrm.potential_starting_users is null',
'                or',
'                (',
'                    dgrm.potential_starting_users is not null',
'                    and',
'                    nv(''APP_USER'') member of apex_string.split(lower(dgrm.potential_starting_users),'':'')',
'                )',
'            )',
'            and',
'            (',
'                dgrm.potential_starting_groups is null',
'                or',
'                (',
'                    dgrm.potential_starting_groups is not null',
'                    and',
'                    exists',
'                    (',
'                        select 1 ',
'                          from apex_string.split(lower(dgrm.potential_starting_groups),'':'') sg',
'                             , apex_appl_acl_users au ',
'                         where lower(au.USER_NAME) = nv(''APP_USER'') ',
'                           and sg.column_value member of apex_string.split(lower(au.role_names),'','')',
'                    )',
'                )',
'            )',
'            and',
'            (',
'                dgrm.excluded_starting_users is null',
'                or',
'                (',
'                    dgrm.excluded_starting_users is not null',
'                    and',
'                    nv(''APP_USER'') not member of apex_string.split(lower(dgrm.excluded_starting_users),'':'')',
'                )',
'            )',
'       )',
' order by dgrm.dgrm_name asc, dgrm.dgrm_version asc;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "FORM_STARTED_PROCESSES_VW" ("PRCS_ID", "PRCS_NAME", "DGRM_ID", "DGRM_NAME", "DGRM_VERSION", "DGRM_STATUS", "PRCS_PRIORITY", "DGRM_CATEGORY", "PRCS_STATUS", "PRCS_INIT_TS", "PRCS_INIT_BY", "PRCS_START_TS", "'
||'PRCS_DUE_ON", "PRCS_COMPLETE_TS", "PRCS_LAST_UPDATE", "PRCS_LAST_UPDATE_BY", "PRCS_BUSINESS_REF") AS ',
'  select flow_inst.prcs_id',
'        ,flow_inst.prcs_name',
'        ,flow_inst.dgrm_id',
'        ,flow_inst.dgrm_name',
'        ,flow_inst.dgrm_version',
'        ,flow_inst.dgrm_status',
'        ,flow_inst.prcs_priority',
'        ,flow_inst.dgrm_category',
'        ,flow_inst.prcs_status',
'        ,flow_inst.prcs_init_ts',
'        ,flow_inst.prcs_init_by',
'        ,flow_inst.prcs_start_ts',
'        ,flow_inst.prcs_due_on',
'        ,flow_inst.prcs_complete_ts',
'        ,flow_inst.prcs_last_update',
'        ,flow_inst.prcs_last_update_by',
'        ,flow_inst.prcs_business_ref',
'    from flow_instances_vw flow_inst',
'    join flow_instance_variables_vw flow_vars',
'      on flow_inst.prcs_id = flow_vars.prov_prcs_id',
'   where prov_var_name = ''STARTED_FROM''',
'     and prov_var_vc2 = ''Simple Process Starter'';',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "FORM_TEMPLATES_LOV" ("SFTE_ID", "SFTE_NAME", "SFTE_CONTENT") AS ',
'  select sfte_id',
'        ,sfte_name',
'        ,sfte_content',
'  from flow_simple_form_templates',
'  with read only; ',
'  ',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "FORM_P0001_VW" ("DGRM_ID", "DGRM_NAME", "DGRM_SHORT_DESCRIPTION", "DGRM_DESCRIPTION", "DGRM_ICON", "DGRM_CATEGORY") AS ',
'  select dgrm_id',
'       , dgrm_name',
'       , dgrm_short_description',
'       , dgrm_description',
'       , dgrm_icon',
'       , dgrm_category',
'  from form_startable_diagrams_vw;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "FORM_P0005_VW" ("INST_ID", "INST_FTPL_ID", "FTPL_SFTE_ID", "FTPL_CONTENT", "PRCS_ID", "PRCS_STATUS", "DGRM_NAME", "PRCS_NAME", "FDTA_ID", "PRCS_INIT_TS", "PRCS_INIT_BY") AS ',
'  select form_inst.inst_id',
'        ,form_inst.inst_ftpl_id',
'        ,ft.ftpl_sfte_id',
'        ,sfte.sfte_content as ftpl_content',
'        ,form_proc.prcs_id',
'        ,form_proc.prcs_status ',
'        ,form_proc.dgrm_name',
'        ,form_proc.prcs_name',
'        ,fdta.fdta_id',
'        ,form_proc.prcs_init_ts',
'        ,form_proc.prcs_init_by',
'  from form_instances form_inst',
'  left join form_started_processes_vw form_proc on form_inst.inst_id      = to_number(form_proc.prcs_business_ref default -1 on conversion error)',
'  left join form_data          fdta             on form_inst.inst_id      = fdta.fdta_inst_id and fdta_task_bpmn_id is null',
'  left join form_templates ft                   on form_inst.inst_ftpl_id = ft.ftpl_id',
'  left join flow_simple_form_templates sfte     on ft.ftpl_sfte_id        = sfte.sfte_id;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "FORM_P0008_VW" ("DGRM_ID", "DGRM_CONTENT") AS ',
'  select dgrm.dgrm_id',
'        ,dgrm.dgrm_content',
'  from flow_diagrams dgrm',
'with read only;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "FORM_P0010_VW" ("FDTA_ID", "FDTA_INST_ID", "FDTA_CONTENT", "FDTA_PROCESS_NAME", "FDTA_TASK_NAME", "FDTA_TASK_BPMN_ID", "FDTA_TEMPLATE_NAME", "FDTA_TEMPLATE_STATIC_ID", "FDTA_CREATED_ON", "FDTA_CREATED_BY") '
||'AS ',
'  select fdta_id',
'        ,fdta_inst_id',
'        ,fdta_content',
'        ,fdta_process_name',
'        ,fdta_task_name',
'        ,fdta_task_bpmn_id',
'        ,fdta_template_name',
'        ,fdta_template_static_id',
'        ,fdta_created_on',
'        ,fdta_created_by',
'  from form_data;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "FORM_P0011_VW" ("FDTA_ID", "FDTA_CONTENT", "FDTA_TEMPLATE_CONTENT") AS ',
'  select fdta.fdta_id',
'        ,fdta.fdta_content',
'        ,fdta.fdta_template_content',
'  from form_data fdta;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "FORM_P0020_VW" ("FTPL_ID", "FTPL_DGRM_NAME", "FTPL_DGRM_VERSION", "SFTE_NAME", "SFTE_STATIC_ID", "SFTE_CONTENT") AS ',
'  select ft.ftpl_id',
'      ,ft.ftpl_dgrm_name',
'      ,ft.ftpl_dgrm_version',
'      ,sfte.sfte_name',
'      ,sfte.sfte_static_id',
'      ,sfte.sfte_content',
'  from form_templates ft',
'  join flow_simple_form_templates sfte on sfte.sfte_id = ft.ftpl_sfte_id',
'with read only;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "FORM_P0021_VW" ("FTPL_ID", "FTPL_DGRM_NAME", "FTPL_DGRM_VERSION", "SFTE_ID", "SFTE_CONTENT") AS ',
'  select ft.ftpl_id',
'        ,ft.ftpl_dgrm_name',
'        ,ft.ftpl_dgrm_version',
'        ,sfte_id',
'        ,sfte.sfte_content',
'  from form_templates ft',
'  join flow_simple_form_templates sfte on sfte.sfte_id = ft.ftpl_sfte_id',
'with read only;'))
);
